mapper.insert:
INSERT
  INTO TBL_USER 
(
  USER_CODE
, USER_ID
, USER_PWD
, USER_NAME
, USER_PHONE
)
VALUES
(
  SEQ_USER_CODE.NEXTVAL
, #{ username }
, #{ password }
, #{ name }
, #{ phone }
)

mapper.update:
UPDATE 
      TBL_USER 
  SET USER_NAME = #{ name }
    , USER_PHONE = #{ phone }
WHERE USER_STATUS = 'Y'
  AND USER_CODE = #{ memberNo }
